var count=0,countsSpan=document.getElementById("counts"),popup=document.getElementById("popup"),overlay=document.getElementById("overlay"),btnShow=document.getElementById("btn-show"),btnClose=document.getElementById("btn-close"),btnReset=document.getElementById("btn-reset");function showPopup(){overlay.classList.add("show"),popup.style.display="block"}function hidePopup(){popup.style.display="none",overlay.classList.remove("show")}btnShow.onclick=function(){count+=1,5==(countsSpan.innerHTML=count)&&(btnReset.style.display="block"),showPopup()},btnClose.onclick=function(){hidePopup()},btnReset.onclick=function(){count=0,countsSpan.innerHTML=count,this.style.display="none"},overlay.onclick=function(){hidePopup()};